void initGPIOB(void)
{
	GPIO_InitTypeDef GPIOB_init;

	RCC_AHBPeriphClockCmd(RCC_AHBPeriph_GPIOB ,ENABLE);

	GPIOB_init.GPIO_Pin = GPIO_Pin_0|GPIO_Pin_1|GPIO_Pin_2|GPIO_Pin_10;
	GPIOB_init.GPIO_Mode = GPIO_Mode_OUT;
	GPIOB_init.GPIO_OType = GPIO_OType_PP;
	GPIOB_init.GPIO_PuPd = GPIO_PuPd_NOPULL;
	GPIOB_init.GPIO_Speed = GPIO_Speed_2MHz;

	GPIO_Init(GPIOB, &GPIOB_init);
}
//-------------------------------------------------------------
void initGPIO(void)
{
	GPIO_InitTypeDef GPIOB_init;

	RCC_AHBPeriphClockCmd(RCC_AHBPeriph_GPIOA ,ENABLE);
	RCC_APB2PeriphClockCmd(RCC_APB2Periph_SPI1, ENABLE);

	GPIOB_init.GPIO_Pin = GPIO_Pin_5|GPIO_Pin_7;
	GPIOB_init.GPIO_Mode = GPIO_Mode_AF;
	GPIOB_init.GPIO_OType = GPIO_OType_PP;
	GPIOB_init.GPIO_PuPd = GPIO_PuPd_DOWN;
	GPIOB_init.GPIO_Speed = GPIO_Speed_2MHz;

	GPIO_Init(GPIOA, &GPIOB_init);
}
