	void SetText(char text[], uint8_t Y, uint8_t X)
	{
		CleanLCD();

		SPISend(0x80 + X);   // x = 0
		SPISend(0x40 + Y);   // y = 0


		DCOn();
		uint16_t j;
		uint16_t ii;
		char ch;

	int cnt = strlen(text);

		for(ii=0;ii < cnt ;ii++)
		{
		 for(j = 0; j < 5; j++){

			ch = text[ii];
			 if(ch>0x7d)
			 {
				 ch = ch - 0xc0 + 94;
			 }
			 else{
				 ch = ch-0x20;
			 }
			 SPISend((uint8_t) Font[((uint8_t)ch)][j]); //en(-0x20) rus(-0xc0 + 94) strlen
		 }
		 SPISend(0x00);
		}
		uint8_t a;
		for(a=0;a<256;a++);
		DCOff();
	 }
